import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import Loading from '../components/Loading';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import { addSong } from '../services/favoriteSongsAPI';

class Album extends Component {
  constructor() {
    super();

    this.state = {
      album: [],
      albumInfo: {},
      loading: true,
      musicFav: false,
      favoriteList: [],
    };
  }

  componentDidMount() {
    this.getMusicAPI();
  }

  getMusicAPI = async () => {
    const { match: { params } } = this.props;
    const albumSearchFull = await getMusics(params.id);
    const albumSearch = albumSearchFull.slice(1);
    this.setState({
      album: albumSearch,
      albumInfo: albumSearchFull[0],
      loading: false,
    });
  };

  handleCheckbox = async ({ target }) => {
    this.setState(
      { loading: true },
      async () => {
        const { album } = this.state;
        const objMusic = album
          .find((music) => Number(target.id) === Number(music.trackId));
        console.log(objMusic);
        addSong(objMusic);
        this.setState((previus) => ({
          loading: false,
          musicFav: !previus.musicFav,
          favoriteList: [...previus.favoriteList, objMusic],
        }));
      },
    );
  }

  render() {
    const { loading, album, musicFav, albumInfo } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        {loading ? <Loading /> : (
          <div>
            <div>
              <img src={ album[0].artworkUrl100 } alt={ album[0].collectionName } />
              <h1 data-testid="album-name">{albumInfo.collectionName}</h1>
              <h3 data-testid="artist-name">{albumInfo.artistName}</h3>
            </div>
            <div>
              {album.map((music) => (<MusicCard
                key={ music.trackId }
                music={ music }
                funcFavSong={ this.handleCheckbox }
                musicFav={ musicFav }
              />))}
            </div>
          </div>
        )}
      </div>
    );
  }
}

Album.propTypes = {
  id: PropTypes.string.isRequired,
  match: PropTypes.objectOf(PropTypes.object).isRequired,
  params: PropTypes.objectOf(PropTypes.object).isRequired,
};

export default Album;
