import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import Loading from '../components/Loading';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import { addSong } from '../services/favoriteSongsAPI';

class Album extends Component {
  constructor() {
    super();

    this.state = {
      album: [],
      loading: true,
      musicFav: false,
    };
  }

  componentDidMount() {
    this.getMusicAPI();
  }

  getMusicAPI = async () => {
    const { match: { params } } = this.props;
    const albumSearchFull = await getMusics(params.id);
    const albumSearch = albumSearchFull.slice(1);
    console.log(albumSearch);
    this.setState({
      album: albumSearch,
      loading: false,
    });
  };

  handleCheckbox = async () => {
    this.setState(
      { loading: true },
      async () => {
        const { album } = this.state;
        const objMusic = album.find((music) => music.trackId);
        addSong(objMusic);
        this.setState({
          loading: false,
          musicFav: !musicFav ? true : musicFav,
        });
      },
    );
  }

  render() {
    const { loading, album, musicFav } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        {loading ? <Loading /> : (
          <div>
            <div>
              <img src={ album[0].artworkUrl100 } alt={ album[0].collectionName } />
              <h1 data-testid="album-name">{album[0].collectionName}</h1>
              <h3 data-testid="artist-name">{album[0].artistName}</h3>
            </div>
            <div>
              {album.map((music) => (<MusicCard
                key={ music.trackId }
                music={ music }
                funcFavSong={ this.handleCheckbox }
                musicFav={ musicFav }
              />))}
            </div>
          </div>
        )}
      </div>
    );
  }
}

Album.propTypes = {
  id: PropTypes.string.isRequired,
  match: PropTypes.objectOf(PropTypes.object).isRequired,
  params: PropTypes.objectOf(PropTypes.object).isRequired,
};

export default Album;
